import { useState, useEffect } from "react";
import axios from "axios";
import Link from "next/link";
import Router from "next/router";

import { ToastContainer, toast } from "react-toastify";
import { API } from "../config.js";
import Layout from "../components/Layout";
import LoginBack from "../components/backgrounds/LoginBack";
import classes from "./login.module.css";

const Login = () => {
  const [formState, setFormState] = useState({
    email: "",
    password: "",
    error: "",
    success: "",
    buttonText: "Login",
  });

  const { email, password, error, success, buttonText } = formState;

  useEffect(() => {
    {
      success &&
        toast.success(`${success}`, {
          position: "top-right",
          autoClose: 5000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
        });
    }
    {
      error &&
        toast.error(`${error}`, {
          position: "top-right",
          autoClose: 5000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
        });
    }
  }, [success, error]);

  const handleFormInputChange = (name) => (e) => {
    setFormState({
      ...formState,
      [name]: e.target.value,
      error: "",
      success: "",
    });
  };

  const handleFormSubmit = async (e) => {
    setFormState({ ...formState, buttonText: "Sending!" });
    e.preventDefault();
    try {
      const response = await axios.post(`${API}/register`, {
        name,
        email,
        password,
      });
      setFormState({
        ...formState,
        name: "",
        email: "",
        password: "",
        buttonText: "Submitted",
        success: response.data.message,
      });
    } catch (error) {
      console.log(error);
      setFormState({ ...formState, error: error.response.data.error });
    }
  };

  return (
    <Layout>
      <LoginBack className={classes.loginBG} />
      <div className={classes.loginLayout}>
        <form className={classes.loginForm}>
          <h2>Login Here</h2>
          <input type="email" className="form-control" name="email" />
          <input type="password" className="form-control" name="password" />
        </form>
        <ToastContainer />
      </div>
    </Layout>
  );
};

export default Login;
